package edu.jorbonism.nfclanders.enums

import edu.jorbonism.nfclanders.getFlag
import edu.jorbonism.nfclanders.numberFromFlags

data class ToyType(
    val name        : String,
    val characterID : UInt,
    val deco        : Deco,
    val game        : Game,
    val repose      : Boolean = false,
    val variant     : Boolean = false,
    val lightcore   : Boolean = false,
    val supercharger: Boolean = false,
    val nonUnique   : Boolean = false,
) {
    fun variantFlagByte(): Byte {
        return ((game.ord.toULong() shl 4) or numberFromFlags(arrayOf(supercharger, lightcore, variant, repose))).toByte()
    }

    companion object {
        fun fromData(characterID: UInt, decoID: Byte, flags: Byte): ToyType {
            val deco = Deco.entries[decoID.toInt()]
            val game = Game.entries[(flags.toInt() shr 4) and 0b1111]
            val repose       = getFlag(flags, 3)
            val variant      = getFlag(flags, 2)
            val lightcore    = getFlag(flags, 1)
            val supercharger = getFlag(flags, 0)

            for (entry in table) {
                if (
                    entry.characterID  == characterID  &&
                    entry.deco         == deco         &&
                    entry.game         == game         &&
                    entry.repose       == repose       &&
                    entry.variant      == variant      &&
                    entry.lightcore    == lightcore    &&
                    entry.supercharger == supercharger
                ) return entry
            }
            return ToyType("Unknown", characterID, deco, game, repose, variant, lightcore, supercharger)
        }

